func solvePartOne(instructions string) (int, error) {
	floor := 0
	for _, c := range instructions {

		switch string(c) {
		case "(":
			floor = floor + 1
		case ")":
			floor = floor - 1

		default:
			return 0, errors.New("unknown instruction")

		}

	}
	return floor, nil

}

func solvePartTwo(instructions string, target int) (int, error) {
	floor := 0
	for i, c := range instructions {
		switch string(c) {
		case "(":
			floor = floor + 1
		case ")":
			floor = floor - 1
		default:
			return 0, errors.New("unknown instruction")
		}

		if floor == target {
			return i + 1, nil
		}
	}
	return 0, errors.New("Never reached target floor")
}

func main() {
	floor, err := solvePartTwo(INSTRUCTIONS, -1)
	if err != nil {
		panic(err)
	}
	fmt.Println(floor)
}
