def follow_instructions(instructions: str) -> int:
    floor = 0
    for char in instructions:
        if char == "(":
            floor = floor + 1
        elif char == ")":
            floor = floor - 1
        else:
            raise ValueError("unknown instruction {}".format(char))
    return floor


def index_of_floor(instructions: str, target: int) -> int:
    floor = 0
    for idx, char in enumerate(instructions):
        if char == "(":
            floor = floor + 1
        elif char == ")":
            floor = floor - 1
        else:
            raise ValueError("unknown instruction {}".format(char))
        if floor == target:
            return idx + 1


def part_one():
    result = follow_instructions(INSTRUCTIONS)
    print(result)


def part_two():
    result = index_of_floor(INSTRUCTIONS, -1)
    print(result)


if __name__ == "__main__":
    part_two()
